<script>import { twMerge } from "tailwind-merge";
export let aClass = "flex items-center";
export let spanClass = "self-center text-2xl font-semibold whitespace-nowrap dark:text-white";
export let imgClass = "me-3 h-8";
export let href = "";
export let src = "";
export let alt = "";
export let name = "";
export let target = "";
export let classA = "";
export let classSpan = "";
export let classImg = "";
let aCls = twMerge(aClass, classA);
let spanCls = twMerge(spanClass, classSpan);
let imgCls = twMerge(imgClass, classImg);
</script>

{#if href}
  <a {...$$restProps} {href} {target} class={aCls}>
    <img {src} class={imgCls} {alt} />
    <span class={spanCls}>{name}</span>
    <slot />
  </a>
{:else}
  <img {...$$restProps} {src} class={imgCls} {alt} />
{/if}

